import React, { useState } from 'react'
export default function Textform() {
    const [text, setText] = useState("Enter text here ");
    // setText("This is my text");
    const Change=() =>{
        console.log('clicked');
        setText("uppercase");
            }
    return (
        <>
            <div className="form-group green-border-focus">
                <label for="exampleFormControlTextarea5">Enter text here</label>
                <textarea className="form-control" id="mybox" rows="3" value={text} onChange={Change}></textarea>
            </div>
        </>
    )
}
